document.addEventListener("DOMContentLoaded",(async()=>{for(const closeEl of document.querySelectorAll(".alert .icofont-close"))closeEl.addEventListener("click",(event=>event.currentTarget.parent(".alert").remove()),!1),closeEl.addEventListener("touchstart",(event=>event.currentTarget.parent(".alert").remove()),!!supportsPassive&&{passive:!0})}),!1);